// mui
import { Tooltip } from '@mui/material';

// react
import { FC } from 'react';

interface SocialMediaIconProps {
  imgSrc: string;
  title: string;
}

const SocialMediaIcon: FC<SocialMediaIconProps> = ({ imgSrc, title }) => {
  return (
    <Tooltip title={title} placement="bottom" arrow>
      <div className="flex items-center justify-center text-white h-12 w-12 rounded-full bg-icons cursor-pointer hover:bg-secondary transition-all ease-linear duration-300">
        <img src={imgSrc} alt={title} />
      </div>
    </Tooltip>
  );
};

export default SocialMediaIcon;
