// assets
import React from 'react';
import contactPageImg from '../assets/image/contact-page.svg';
import contactIllustration from '../assets/image/contact-icon.svg';

// components
import { Button, LabelInput, Reveal } from '../components';

// framer-motion
import { motion } from 'framer-motion';

// utils
import { fadeIn, scale } from '../utils/variants';
import { transition } from '../utils/transition';

const Contact = () => {
  return (
    <div
      id="contact"
      className="min-h-screen flex items-center justify-center relative pb-20"
      style={{
        background: `url(${contactPageImg})`,
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
      }}
    >
      <div className="max-w-screen-2xl flex flex-col xl:flex-row xl:justify-between items-center xl:items-start gap-12 w-full pt-20 pb-20 sm:pb-16 px-12">
        <div className="flex-1 flex flex-col gap-4">
          <Reveal>
            <h2 className="text-center xl:text-start text-4xl sm:text-5xl lg:text-[64px] font-bold text-textPrimary">
              Get in <span className="text-secondary">touch</span>
            </h2>
          </Reveal>
          <motion.img
            variants={scale()}
            transition={transition}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: false }}
            src={contactIllustration}
            alt="Contact Image"
            className="max-h-[348px]"
          />
        </div>
        <motion.div
          variants={fadeIn('up')}
          transition={transition}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: false }}
          className="flex-1 flex flex-col w-full max-w-[696px] gap-6"
        >
          <div className="flex flex-col sm:flex-row items-center gap-6">
            <LabelInput labelText="Your name" placeholderText="Name" />
            <LabelInput labelText="Your email" placeholderText="Email" />
          </div>
          <div className="flex flex-col sm:flex-row items-center gap-6">
            <LabelInput
              labelText="Your message"
              placeholderText="Write a message..."
              textarea
            />
          </div>
          <Button secondary>Send Message</Button>
        </motion.div>
      </div>
      <div className=" absolute bottom-0 left-0 w-full h-[1px] bg-divider" />
    </div>
  );
};

export default Contact;
